insert into `language_translation`(`id`,`language_id`,`catalogue`,`translation`,`languageToken_id`) values (43,1,'messages','We are the main Panama distributors of mass consumer products, food and beverage, perfumes and cosmetics, automotive products and liquors; and leaders in each category.</br></br>
